import React from 'react';
import classes from './App.module.css';
import Linegraph from './'
var csvUrl = 'https://raw.githubusercontent.com/nytimes/covid-19-data/master/us-states.csv';

function handleCSVResult(csvString) {
  // Get the div element to append the data to
  var dataArea = document.getElementById('csv-data');

  // Split csv to rows array
  var rows = csvString.split('\n');
  //console.log(rows);
  let newRows = rows.map(element => element.split(","));
  console.log(newRows);
  let washington = [];
  for (let i = 1; i < newRows.length; i++) {
    if (newRows[i][1] === "Washington") {
      washington.push(newRows[i]);
    }
    //washington = newRows.filter(element => element[i][1] === "Washington");
  }
  //rows.filter(row => row[1] === "Washington");
  console.log(washington);
  var htmlStr = '';

  // Iterate over each row
  for (var i = 0; i < rows.length; i++) {
    var row = rows[i];

    //console.log(row);
    // split row to cells
    var cells = row.split(',');

    // Extract data from cells of current row
    var date = cells[0];
    var state = cells[1];
    // eslint-disable-next-line
    var fips = cells[2];
    var cases = cells[3];
    var deaths = cells[4];

    // Add extracted CSV data to string
    htmlStr += `<p>Date: ${date}, state: ${state}, cases: ${cases}, deaths: ${deaths}</p><br>`;
  }

  //console.log(htmlStr);
  // Set the string generated from CSV as HTML of the dedicated div
  dataArea.innerHTML = htmlStr;
}

// Init Ajax Object
var ajax = new XMLHttpRequest();

// Set a GET request to the URL which points to your CSV file
ajax.open('GET', csvUrl);

// Set the action that will take place once the browser receives your CSV
ajax.onreadystatechange = function() {
    if(ajax.readyState === XMLHttpRequest.DONE && ajax.status === 200) {
        // Request was successful
        var csvData = ajax.responseText;

        // Do something with that data here
        handleCSVResult(csvData);
    }
}

// Send request
ajax.send();
function App() {
  return (
    <div className="App" id="csv-data">
      <>
      {}
      </>
    </div>
  );
}

export default App;
